#include <iostream>#include <cmath>using namespace std;int main(){	int k, N, i;	double S;	cout << "k = "; cin >> k;	cout << "N = "; cin >> N;	S = 19;	k = N;	while (k <= 19)	{		S += sqrt((sin(k) * sin(k)) + (cos(N / k) * cos(N / k)));		k++;	}	cout << S << endl;		S = 19;	k = N;	do {		S += sqrt((sin(k) * sin(k)) + (cos(N / k) * cos(N / k)));		k++;	} while (k <= 19);	cout << S << endl;	S = 19;	for (k = N; k <= 19; k++)	{		S += sqrt((sin(k) * sin(k)) + (cos(N / k) * cos(N / k)));	}	cout << S << endl;	S = 19;	for (k = 19; k >= N; k--)	{		S += sqrt((sin(k) * sin(k)) + (cos(N / k) * cos(N / k)));	}	cout << S << endl;	return 0;}